import React, { useEffect, useState } from 'react';
import { View, Text, StyleSheet, ImageBackground, TouchableOpacity, Animated } from 'react-native';
import { StatusBar } from 'expo-status-bar';

export default function Nft({ navigation }) {
    const [isLoading, setLoading] = useState(true);
    const [Nft, setNft] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try{
                const response = await fetch('https://api.coingecko.com/api/v3/nfts/list')
                const resulat = await response.json();

                if(Array.isArray(resulat)){
                    setNft(resulat);
                }else console.error("API response is not an array:");
                setTimeout(() => {
                    setLoading(false);
                }, 500);
            }catch(error){
                console.error("Error fetching NFT:", error);
                setLoading(true);
            }
        };
    }, []);
    console.log(Nft);
    return (
        <View style={styles.container}>
            <Text style={styles.text}>La page des NFT's</Text>
        </View>
    )

}

const styles = StyleSheet.create({
    container: {
        flex: 1,
        alignItems: 'center',
        justifyContent: 'center',
        backgroundColor:'white',
    }, 
    text: {
        color: 'black',
        fontSize: 16,
        fontWeight: 'bold',
    },
})
